class User < ApplicationRecord
  acts_as_authentic

  has_many :comments, dependent: :destroy
  has_many :articles, dependent: :destroy

  validate :avatar_must_be_img

  def avatar_must_be_img
    valid_format = %w[image/gif image/png image/jpeg image/x-icon]
    valid = false
    valid_format.each do |format|
      valid = true if avatar.index(format)
    end
    errors.add(:invalid_image, 'Image format is invalid!') unless valid
  end

  def avatar_src
    if email.nil?
      src = 'data:image/jpeg;base64,'
    elsif gravatar
      require 'digest/md5'
      hash = Digest::MD5.hexdigest(email)
      src = 'https://gravatar.loli.net/avatar/' + hash + '/?s=512'
    else
      src = avatar
    end
    src
  end
end